mapper.update:

  UPDATE 
  	mer_img_tb 
  SET 
  	mer_img_address=#{merImgAddress},
  	mer_img_update_time=#{merImgUpdateTime},
  	mer_id=#{merId},
  	seller_id=#{sellerId}
  WHERE
  	 mer_img_id=#{merImgId}
  AND  seller_id=#{sellerId}

mapper.delete:

  DELETE FROM
  	 mer_img_tb 
  	 WHERE mer_img_id=#{merImgId}
  	 AND  seller_id=#{sellerId}

mapper.select:
  
    SELECT
	      mer_img_id,
		  mer_img_address,
     	  mer_img_update_time,
   		  mer_id
	FROM mer_img_tb
	WHERE mer_id=#{merId}
	AND  seller_id=#{sellerId}
	ORDER BY
		${orderName}  ${orderWay}
	LIMIT
		#{pageNum},#{pageSize}

  
    SELECT
          mer_img_id,
		  mer_img_address,
     	  mer_img_update_time,
   		  mer_id
	FROM mer_img_tb
	WHERE mer_id=#{merId}
	AND  seller_id=#{sellerId}
	ORDER BY
		${orderName} ${orderWay}
